//import { createStackNavigator } from '@react-navigation/stack';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import { imageIndex } from '../constants/images';
// Import navigation and screens
import ViewPeopleScreen from '../screens/ViewPeopleScreen';

// Import styling and components
import Styles from "../styles/MainStyle";

//const Stack = createStackNavigator();
const Stack = createNativeStackNavigator();
const CustomHeader = () => {
    // Your custom header component with a right-aligned title
    return (
      <View style={{ flexDirection: 'row', justifyContent: 'flex-end', marginRight: 10 }}>
        <Text style={{ fontSize: 18, fontWeight: 'bold' }}>Your Title</Text>
      </View>
    );
  };
  
  const CustomImageHeader = () => {
    return (
      <View style={{ marginRight: 10 }}>
        <Image
          source={require('../assets/images/icon.png')} // Replace with your image path
          style={{ width: 40, height: 40, borderRadius: 20 }} // Adjust dimensions and styles
        />
      </View>
    );
  };
  
export default function PeopleNavigator() {
    return (
        
        <Stack.Navigator 
        initialRouteName="ViewPeople"
        screenOptions={{
            headerShown: true,
            headerBackTitle: "Back",
            headerStyle: Styles.headerBar,
            headerTitleStyle: Styles.headerBarTitle,

        }}>
                  

        <Stack.Screen
            name="ViewPeople"
            component={ViewPeopleScreen}
            options={{
                header: () => <CustomHeader />, // Use the header property for custom header
              }}
              />           
        </Stack.Navigator>

    );
}
