//import { createStackNavigator } from '@react-navigation/stack';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import { imageIndex } from '../constants/images';
// Import navigation and screens
import ViewPeopleScreen from '../screens/ViewPeopleScreen';
import HelpScreen from '../screens/HelpScreen';
// Import styling and components
import Styles from "../styles/MainStyle";
import HomeScreen from '../screens/HomeScreen';

//const Stack = createStackNavigator();
const Stack = createNativeStackNavigator();
const HeaderBar = () => 
{
    return (
        // what is shown here is basically a box within a box changing the styling of the box as the boxes go deeper 
        // the key word 'View' are the boxes
        <View style={{ backgroundColor: 'white',  borderBottomColor: 'lightgray', borderBottomWidth: 1, }}>
            <View style={{flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', height: 25, marginTop: 25, marginBottom: 25, marginLeft: 25, marginRight: 25}}>

                <Image
                    source={require('../assets/images/roi-logo.jpg')} 
                    style={{ width: 100, height: 100, resizeMode: 'contain' }}
                /> 
                <Text style={[Styles.headerBarTitle]}>View All Staff</Text>

            </View>
        </View>
    );
};

  
export default function PeopleNavigator() {
    return (

        <Stack.Navigator 
            initialRouteName="ViewPeople"
            screenOptions={{
            }}>
                  

        <Stack.Screen
            name="ViewPeople"
            component={ViewPeopleScreen}
           
            options={{
                header: () => <HeaderBar />, // using a custom component to change the styling to better reflect the figma design
              }}
              />        

        <Stack.Screen
            name="Help" 
            component={HelpScreen}
           
            options={{ title: 'View All Staff' }}/>
              />                 
        </Stack.Navigator>        

    );
}
