//import { createStackNavigator } from '@react-navigation/stack';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import { imageIndex } from '../constants/images';
// Import navigation and screens
import ViewPeopleScreen from '../screens/ViewPeopleScreen';

// Import styling and components
import Styles from "../styles/MainStyle";

//const Stack = createStackNavigator();
const Stack = createNativeStackNavigator();
const Styles1 = StyleSheet.create({
    headerBarTitle: {
      color: 'black', // Example color value
      fontSize: 18, // Example font size
      // Other style properties
    },
    underline: {
      borderBottomColor: 'gray',
      borderBottomWidth: 1,
      marginTop: 5, // Adjust spacing between text and line
      width: '50%', // Adjust width as needed
    },
  });
  
  const CustomHeader = () => {
    return (
      <View style={{ alignItems: 'center', marginBottom: 25 }}>
        <Image
          source={require('../assets/images/roi-logo.jpg')} 
          style={{ width: 100, height: 100, resizeMode: 'contain' }}
        />
        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
          <Text style={Styles.headerBarTitle}>View All Staff</Text>
          <View style={Styles.underline} />
        </View>
      </View>
    );
  };

  
export default function PeopleNavigator() {
    return (
        
        <Stack.Navigator 
        initialRouteName="ViewPeople"
        screenOptions={{
            headerShown: true,
            headerBackTitle: "Back",
            headerStyle: Styles.headerBar,
            headerTitleStyle: Styles.headerBarTitle,

        }}>
                  

        <Stack.Screen
            name="ViewPeople"
            component={ViewPeopleScreen}
            options={{
                header: () => <CustomHeader />, 
              }}
              />           
        </Stack.Navigator>

    );
}
